window.onload=function()
{var promises=[];var ngApp=document.querySelectorAll('[ng-app]')[0];var ngControllers=document.querySelectorAll('[ng-controller]');if(typeof angular=='undefined')
{console.warn("simplify error: Angular not found, operation canceled.");return}
if(ngApp)
{console.warn("Please remove the ng-app directive. `Simplify` will bootstrap the application manually.");console.warn("This will also most likely fix the 'Uncaught Error: [$injector:modulerr]' error.")}
for(var i=0;i<ngControllers.length;i++)
{var promise=new Promise(function(resolve,reject){var src='src/application/controllers/'+ngControllers[i].getAttribute('ng-controller')+'.controller.js';var script=document.createElement('script');script.setAttribute('src',src);document.head.appendChild(script);script.addEventListener('load',function(){resolve(script)})});promises.push(promise)}
Promise.all(promises).then(function(){angular.bootstrap(document,['app'])})}
